$(document).ready(function(){$('#busca-cep').submit(function(){if($('#cep').val().length===8){$.ajax({url:'https://viacep.com.br/ws/'+$('#cep').val()+'/json/',dataType:'json',success:function(d){if(d){var cardBusca=$('#card-busca');var cardResult=$('#card-resultado');cardBusca.removeClass('offset-m3');cardResult.fadeIn();cardBusca.scrollTop(330);complemento=(d.complemento!=="")?' - '+d.complemento:'';$('#card-resultado p').html('<strong>Endereço:</strong> '+d.logradouro+complemento+'<br />'+'<strong>Bairro:</strong> '+d.bairro+'<br />'+'<strong>Cidade:</strong> '+d.localidade+' - '+d.uf+'<br />'+'<strong>CEP:</strong> '+d.cep);var APIKey='AIzaSyBxp983W4Ppj3r0d6JPBDNkZa2XmHVAeHc';$('#card-resultado p').append('<iframe width="100%" height="400" frameborder="0" style="border:0" '+'src="https://www.google.com/maps/embed/v1/search?key='+APIKey+'&q='+d.logradouro+' '+d.bairro+' '+d.localidade+' '+d.uf+'" allowfullscreen></iframe>');}}});}else{Materialize.toast('Insira um CEP válido',4000,'',function(){$('#cep').focus();});}return false;});});